import React, { createContext, useState } from 'react';

export const InventoryContext = createContext();

export const InventoryProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const loginUser = () => setIsAuthenticated(true);
  const logoutUser = () => setIsAuthenticated(false);

  const addProduct = (product) => setProducts([...products, product]);

  const updateProduct = (updatedProduct) => {
    setProducts(products.map(product => 
      product.id === updatedProduct.id ? updatedProduct : product
    ));
  };

  const deleteProduct = (id) => setProducts(products.filter(product => product.id !== id));

  const addStockTransaction = (transaction) => {
    setTransactions([...transactions, transaction]);
    updateStock(transaction.productId, transaction.quantity, transaction.type);
  };

  const updateStock = (id, quantity, type) => {
    setProducts(products.map(product => {
      if (product.id === id) {
        const updatedQuantity = type === 'add' 
          ? product.quantity + quantity 
          : product.quantity - quantity;
        return { ...product, quantity: updatedQuantity };
      }
      return product;
    }));
  };

  return (
    <InventoryContext.Provider value={{
      products,
      addProduct,
      updateProduct,
      deleteProduct,
      transactions,
      addStockTransaction,
      updateStock, // Make sure updateStock is included here
      isAuthenticated,
      loginUser,
      logoutUser,
    }}>
      {children}
    </InventoryContext.Provider>
  );
};
