import React, { useState, useEffect } from 'react';
import { useInventory } from '../context/InventoryContext';

const ProductManagement = () => {
  const { products, updateProducts, addProduct, updateProduct, deleteProduct } = useInventory();
  const [productForm, setProductForm] = useState({
    name: '',
    description: '',
    category: '',
    price: '',
    quantity: '',
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editingProductId, setEditingProductId] = useState(null);

  useEffect(() => {
    // Fetch products when the component mounts
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5002/api/products');
        const data = await response.json();
        updateProducts(data); // Update context state with fetched products
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    fetchProducts();
  }, [updateProducts]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProductForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isEditing) {
      // Update existing product
      try {
        await fetch(`http://localhost:5002/api/products/${editingProductId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productForm),
        });
        updateProduct({ ...productForm, id: editingProductId });
        setIsEditing(false);
        setEditingProductId(null);
      } catch (error) {
        console.error('Error updating product:', error);
      }
    } else {
      // Add new product
      try {
        const response = await fetch('http://localhost:5002/api/products', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(productForm),
        });
        const data = await response.json();
        addProduct({ ...productForm, id: data.productId });
      } catch (error) {
        console.error('Error adding product:', error);
      }
    }
    setProductForm({ name: '', description: '', category: '', price: '', quantity: '' });
  };

  const handleEdit = (product) => {
    setProductForm(product);
    setIsEditing(true);
    setEditingProductId(product.id);
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5002/api/products/${id}`, {
        method: 'DELETE',
      });
      deleteProduct(id);
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <h1>Product Management</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={productForm.name}
          onChange={handleChange}
          placeholder="Product Name"
          required
        />
        <input
          type="text"
          name="description"
          value={productForm.description}
          onChange={handleChange}
          placeholder="Description"
          required
        />
        <input
          type="text"
          name="category"
          value={productForm.category}
          onChange={handleChange}
          placeholder="Category"
          required
        />
        <input
          type="number"
          name="price"
          value={productForm.price}
          onChange={handleChange}
          placeholder="Price"
          required
        />
        <input
          type="number"
          name="quantity"
          value={productForm.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          required
        />
        <button type="submit">{isEditing ? 'Update Product' : 'Add Product'}</button>
      </form>

      <h2>Product List</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <p>{product.name}</p>
            <button onClick={() => handleEdit(product)}>Edit</button>
            <button onClick={() => handleDelete(product.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductManagement;
