import React, { createContext, useState, useContext } from 'react';

export const InventoryContext = createContext();

export const useInventory = () => {
  return useContext(InventoryContext);
};

// Provider component
export const InventoryProvider = ({ children }) => {
  const loadProductsFromStorage = () => {
    const storedProducts = localStorage.getItem('products');
    return storedProducts ? JSON.parse(storedProducts) : [];
  };

  const loadAuthenticationStatus = () => {
    const storedAuth = localStorage.getItem('isAuthenticated');
    return storedAuth === 'true'; // Convert from string to boolean
  };

  // State initialization
  const [isAuthenticated, setIsAuthenticated] = useState(loadAuthenticationStatus());
  const [products, setProducts] = useState(loadProductsFromStorage());

  // Product management functions
  const addProduct = async (newProduct) => {
    try {
      const response = await fetch('http://localhost:5002/api/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newProduct),
      });

      if (response.ok) {
        // Fetch latest products after adding a new one
        await fetchProductsFromBackend();
      }
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const fetchProductsFromBackend = async () => {
    try {
      const response = await fetch('http://localhost:5002/api/products');
      const data = await response.json();
      setProducts(data); // Update the state with fetched products
      localStorage.setItem('products', JSON.stringify(data)); // Save to localStorage
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Stock transaction management
  const addStockTransaction = ({ productId, quantity, type }) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) => {
        if (product.id === productId) {
          const updatedQuantity = type === 'add' ? product.quantity + quantity : product.quantity - quantity;
          return { ...product, quantity: Math.max(updatedQuantity, 0) }; // Prevent negative quantities
        }
        return product;
      })
    );
    // Optionally, you can save the updated product list to localStorage if you want
    localStorage.setItem('products', JSON.stringify(products));
  };

  return (
    <InventoryContext.Provider
      value={{
        isAuthenticated,
        products,
        addProduct,
        addStockTransaction,
      }}
    >
      {children}
    </InventoryContext.Provider>
  );
};
