import React, { createContext, useState } from 'react';

export const InventoryContext = createContext();

export const InventoryProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const loginUser = () => setIsAuthenticated(true);
  const logoutUser = () => setIsAuthenticated(false);

  const addProduct = (product) => setProducts([...products, product]);

  const updateProduct = (updatedProduct) => {
    setProducts(products.map(product => 
      product.id === updatedProduct.id ? updatedProduct : product // Update only the matching product
    ));
  };

  const deleteProduct = (id) => {
    setProducts(products.filter(product => product.id !== id)); // Remove product by ID
  };

  // Additional functions like addStockTransaction can remain here

  return (
    <InventoryContext.Provider value={{
      products,
      addProduct,
      updateProduct,
      deleteProduct,
      transactions,
      // other context values...
      isAuthenticated,
      loginUser,
      logoutUser,
    }}>
      {children}
    </InventoryContext.Provider>
  );
};
