import React, { useContext, useState } from 'react';
import axios from 'axios';
import { InventoryContext } from '../context/InventoryContext';
import { useNavigate, Link } from 'react-router-dom';
import '../styles.css';

const Login = () => {
  const { loginUser } = useContext(InventoryContext);
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [isRegistering, setIsRegistering] = useState(false); // New state for toggling between login and register modes
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { username, password } = credentials;

    try {
      if (isRegistering) {
        // Register the user if in registration mode
        const signupResponse = await axios.post('http://localhost:5000/api/signup', { username, password });
        console.log('Signup response:', signupResponse);

        if (signupResponse.status === 200) {
          // After registration, automatically log in the user
          const loginResponse = await axios.post('http://localhost:5000/api/login', { username, password });
          console.log('Login response:', loginResponse);

          if (loginResponse.status === 200) {
            const success = loginUser(credentials);

            if (success) {
              navigate('/'); // Redirect to the dashboard on successful login
            } else {
              setError('Login failed after registration');
            }
          } else {
            setError('Login failed. Server response: ' + loginResponse.data);
          }
        } else {
          setError('Signup failed. Server response: ' + signupResponse.data);
        }
      } else {
        // If not registering, attempt to log in
        const loginResponse = await axios.post('http://localhost:5000/api/login', { username, password });
        console.log('Login response:', loginResponse);

        if (loginResponse.status === 200) {
          const success = loginUser(credentials);

          if (success) {
            navigate('/'); // Redirect to the dashboard on successful login
          } else {
            setError('Login failed');
          }
        } else {
          setError('Login failed. Server response: ' + loginResponse.data);
        }
      }
    } catch (err) {
      console.error('Error:', err);

      if (err.response) {
        setError('Error: ' + (err.response.data || err.response.statusText));
      } else {
        setError('Unknown error');
      }
    }
  };

  return (
    <div className="container">
      <div className="loginBox">
        <div className="loginHead">
          <h1>WINGS CAFE</h1>
          <p>Stock Inventory System</p>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="loginInputContainer">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={credentials.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="loginInputContainer">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={credentials.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="LoginButtonContainer">
            <button type="submit">{isRegistering ? 'Register' : 'Login'}</button>
          </div>
          {/* Toggle between Login and Register forms */}
          <p>
            {isRegistering ? (
              <>
                Already have an account? <span onClick={() => setIsRegistering(false)} style={{ cursor: 'pointer', color: 'blue' }}>Login here</span>
              </>
            ) : (
              <>
                Don't have an account? <span onClick={() => setIsRegistering(true)} style={{ cursor: 'pointer', color: 'blue' }}>Register here</span>
              </>
            )}
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
