import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import '../styles.css';

const StockManagement = () => {
  const { products, addStockTransaction } = useContext(InventoryContext);
  const [transaction, setTransaction] = useState({ productId: '', quantity: '', type: '' });
  const [transactionList, setTransactionList] = useState([]);

  const handleChange = (e) => {
    setTransaction({ ...transaction, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Update the stock in InventoryContext
    addStockTransaction(transaction);

    // Record the transaction locally to display in the UI
    const newTransaction = {
      productId: transaction.productId,
      quantity: parseInt(transaction.quantity, 10),
      type: transaction.type,
      date: new Date().toLocaleString(),
    };
    setTransactionList([...transactionList, newTransaction]);

    // Reset the form
    setTransaction({ productId: '', quantity: '', type: '' });
  };

  return (
    <div>
      <h1>Stock Management</h1>
      <form onSubmit={handleSubmit}>
        <select name="productId" value={transaction.productId} onChange={handleChange} required>
          <option value="">Select Product</option>
          {products.map((prod) => (
            <option key={prod.id} value={prod.id}>{prod.name}</option>
          ))}
        </select>
        <input type="number" name="quantity" placeholder="Quantity" value={transaction.quantity} onChange={handleChange} required />
        <select name="type" value={transaction.type} onChange={handleChange} required>
          <option value="">Select Type</option>
          <option value="add">Add</option>
          <option value="deduct">Deduct</option>
        </select>
        <button type="submit">Record Transaction</button>
      </form>

      <h2>Recorded Transactions</h2>
      <ul>
        {transactionList.map((trans, index) => (
          <li key={index}>
            Product ID: {trans.productId}, Quantity: {trans.quantity}, Type: {trans.type}, Date: {trans.date}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StockManagement;
