// src/components/Dashboard.js
import React, { useContext } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import { Line } from 'react-chartjs-2';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Dashboard.css';

// Import Chart.js components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// Register the components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const { products } = useContext(InventoryContext);
  const lowStockThreshold = 10;

  const lowStockProducts = products.filter(product => product.quantity < lowStockThreshold);

  const stockData = {
    labels: products.map((product) => product.name),
    datasets: [
      {
        label: 'Stock Level',
        data: products.map((product) => product.quantity),
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 2,
        pointBackgroundColor: 'rgba(54, 162, 235, 1)',
      },
    ],
  };

  const stockOptions = {
    responsive: true,
    maintainAspectRatio: true,
    aspectRatio: 2,
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Stock Quantity',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Products',
        },
      },
    },
    layout: {
      padding: 20,
    },
    elements: {
      line: {
        tension: 0.4,
      },
    },
  };

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <div className="stock-overview">
        <h2>Current Stock Levels</h2>
        <div className="chart-container">
          <Line data={stockData} options={stockOptions} />
        </div>
      </div>

      {lowStockProducts.length > 0 && (
        <div className="low-stock-alert">
          <h2>Low Stock Alerts</h2>
          <ul>
            {lowStockProducts.map((product) => (
              <li key={product.id}>
                <strong>{product.name}</strong>: Only {product.quantity} left in stock!
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="stock-carousel">
        <h2>Featured Products</h2>
        <Carousel autoPlay infiniteLoop interval={3000} showThumbs={false} showStatus={false} stopOnHover={true}>
          {products.map((product, index) => (
            <div key={index}>
              <img src={product.imageUrl || 'default-image.jpg'} alt={`Product ${index + 1}`} />
            </div>
          ))}
        </Carousel>
      </div>
    </div>
  );
};

export default Dashboard;
