import React, { createContext, useState } from 'react';

export const InventoryContext = createContext();

export const InventoryProvider = ({ children }) => {
  const [products, setProducts] = useState([]); // Product state
  const [transactions, setTransactions] = useState([]); // Transaction state
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Authentication state

  const addProduct = (product) => {
    setProducts([...products, product]);
  };

  const updateProduct = (updatedProduct) => {
    setProducts(products.map(product => product.id === updatedProduct.id ? updatedProduct : product));
  };

  const deleteProduct = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  // Modify the addStockTransaction function
  const addStockTransaction = (transaction) => {
    setTransactions([...transactions, transaction]);

    // Update product quantity based on transaction type
    setProducts(prevProducts => {
      return prevProducts.map(product => {
        if (product.id === transaction.productId) {
          // Add quantity if transaction type is 'add'
          if (transaction.type === 'add') {
            return { ...product, quantity: product.quantity + parseInt(transaction.quantity) };
          }
          // Deduct quantity if transaction type is 'deduct'
          else if (transaction.type === 'deduct') {
            return { ...product, quantity: Math.max(product.quantity - parseInt(transaction.quantity), 0) }; // Prevent negative stock
          }
        }
        return product;
      });
    });
  };

  const loginUser = () => {
    setIsAuthenticated(true); // Simulate user login
  };

  const logoutUser = () => {
    setIsAuthenticated(false); // Simulate user logout
  };

  return (
    <InventoryContext.Provider value={{ 
      products, addProduct, updateProduct, deleteProduct,
      transactions, addStockTransaction, 
      isAuthenticated, loginUser, logoutUser 
    }}>
      {children}
    </InventoryContext.Provider>
  );
};
