import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import '../styles.css';

const ProductManagement = () => {
  const { products, addProduct, updateProduct, deleteProduct } = useContext(InventoryContext);
  const [product, setProduct] = useState({ id: '', name: '', description: '', category: '', price: '', quantity: '' });
  const [isEditing, setIsEditing] = useState(false);

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      updateProduct(product);
    } else {
      addProduct({ ...product, id: Date.now() });
    }
    setProduct({ id: '', name: '', description: '', category: '', price: '', quantity: '' });
    setIsEditing(false);
  };

  const handleEdit = (product) => {
    setProduct(product);
    setIsEditing(true);
  };

  return (
    <div className="product-management">
      <h2>Product Management</h2>
      <form>
        {/* Product input fields */}
        <label>Product Name:</label>
        <input type="text" placeholder="Enter product name" />

        <label>Description:</label>
        <textarea placeholder="Enter product description"></textarea>

        <label>Price:</label>
        <input type="number" placeholder="Enter product price" />

        <label>Category:</label>
        <select>
          <option>Category 1</option>
          <option>Category 2</option>
        </select>

        <button type="submit">Add Product</button>
      </form>
    </div>
  );
};

export default ProductManagement;
