// src/components/Dashboard.js

import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import { Line } from 'react-chartjs-2';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Dashboard.css';

// Import local images at the top of the file
import product1 from '../assets/yogi.jpeg';
import product2 from '../assets/lolli.jpg';
import product3 from '../assets/sanitary.jpeg';

// Import Chart.js components and register them
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// Register the components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const { products } = useContext(InventoryContext);
  const [clearChart, setClearChart] = useState(false);
  const lowStockThreshold = 10;

  // Calculate low stock products
  const lowStockProducts = products.filter(product => product.quantity < lowStockThreshold);

  // Prepare data for stock overview chart
  const stockData = {
    labels: products.map((product) => product.name),
    datasets: [
      {
        label: 'Stock Level',
        data: clearChart ? [] : products.map((product) => product.quantity), // If clearChart is true, set an empty array to clear the chart
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 2,
        pointBackgroundColor: 'rgba(54, 162, 235, 1)',
      },
    ],
  };

  const stockOptions = {
    responsive: true,
    maintainAspectRatio: true,
    aspectRatio: 2,
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Stock Quantity',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Products',
        },
      },
    },
    layout: {
      padding: 20,
    },
    elements: {
      line: {
        tension: 0.4,
      },
    },
  };

  // Array of local image imports for the carousel
  const imageUrls = [product1, product2, product3];

  // Function to toggle clearing the chart
  const handleClearChart = () => {
    setClearChart(!clearChart); // Toggle between clearing and showing chart data
  };

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      {/* Stock Overview Section */}
      <div className="stock-overview">
        <h2>Current Stock Levels</h2>
        <div className="chart-container">
          <Line data={stockData} options={stockOptions} />
        </div>
        <button onClick={handleClearChart}>
          {clearChart ? 'Show Stock Data' : 'Clear Chart'}
        </button>
      </div>

      {/* Low Stock Alerts */}
      {lowStockProducts.length > 0 && (
        <div className="low-stock-alert">
          <h2>Low Stock Alerts</h2>
          <ul>
            {lowStockProducts.map((product) => (
              <li key={product.id}>
                <strong>{product.name}</strong>: Only {product.quantity} left in stock!
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Rotating Images Section */}
      <div className="stock-carousel">
        <h2>Featured Products</h2>
        <Carousel
          autoPlay
          infiniteLoop
          interval={3000}
          showThumbs={false}
          showStatus={false}
          stopOnHover={true}
        >
          {imageUrls.map((url, index) => (
            <div key={index}>
              <img src={url} alt={`Product ${index + 1}`} />
            </div>
          ))}
        </Carousel>
      </div>
    </div>
  );
};

export default Dashboard;
