import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const ProductManagement = () => {
  const { products, addProduct, updateProduct, deleteProduct } = useContext(InventoryContext);
  const [product, setProduct] = useState({ id: '', name: '', description: '', category: '', price: '', quantity: '' });
  const [isEditing, setIsEditing] = useState(false);

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      updateProduct(product);
    } else {
      addProduct({ ...product, id: Date.now() });
    }
    setProduct({ id: '', name: '', description: '', category: '', price: '', quantity: '' });
    setIsEditing(false);
  };

  const handleEdit = (product) => {
    setProduct(product);
    setIsEditing(true);
  };

  return (
    <div>
      <h1>Product Management</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Product Name" value={product.name} onChange={handleChange} required />
        <input type="text" name="description" placeholder="Description" value={product.description} onChange={handleChange} required />
        <input type="text" name="category" placeholder="Category" value={product.category} onChange={handleChange} required />
        <input type="number" name="price" placeholder="Price" value={product.price} onChange={handleChange} required />
        <input type="number" name="quantity" placeholder="Initial Quantity" value={product.quantity} onChange={handleChange} required />
        <button type="submit">{isEditing ? 'Update Product' : 'Add Product'}</button>
      </form>
      <h2>Product List</h2>
      <ul>
        {products.map((prod) => (
          <li key={prod.id}>
            {prod.name} - {prod.description} - {prod.category} - ${prod.price} - Qty: {prod.quantity}
            <button onClick={() => handleEdit(prod)}>Edit</button>
            <button onClick={() => deleteProduct(prod.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductManagement;
