import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import '../styles.css'; // Make sure to import your styles

const ProductManagement = () => {
  const { products, addProduct } = useContext(InventoryContext); // Access products and addProduct from context
  const [productForm, setProductForm] = useState({
    name: '',
    quantity: 0,
  });

  const handleChange = (e) => {
    // Update productForm state when input changes
    setProductForm({ ...productForm, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const productId = Date.now().toString(); // Generate a unique ID for the product
    addProduct({ id: productId, ...productForm }); // Call addProduct with the new product
    setProductForm({ name: '', quantity: 0 }); // Reset the form
  };

  return (
    <div className="productManagementContainer">
      <h2>Product Management</h2>

      {/* Form to add a new product */}
      <form onSubmit={handleSubmit}>
        <div className="inputGroup">
          <label htmlFor="name">Product Name:</label>
          <input
            type="text"
            name="name"
            value={productForm.name}
            onChange={handleChange}
            placeholder="Enter product name"
            required
          />
        </div>
        <div className="inputGroup">
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="number"
            name="quantity"
            value={productForm.quantity}
            onChange={handleChange}
            placeholder="Enter quantity"
            required
          />
        </div>
        <button type="submit">Add Product</button>
      </form>

      {/* Display the list of products */}
      <h3>Existing Products</h3>
      {products.length > 0 ? (
        <ul className="productList">
          {products.map((product) => (
            <li key={product.id}>
              {product.name} - Quantity: {product.quantity}
            </li>
          ))}
        </ul>
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
};

export default ProductManagement;
