import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const StockManagement = () => {
  const { products, addStockTransaction } = useContext(InventoryContext);
  const [stockUpdate, setStockUpdate] = useState({ productId: '', quantity: 0, type: 'add' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStockUpdate(prev => ({ ...prev, [name]: value }));
  };

  const handleUpdateStock = (e) => {
    e.preventDefault();
    const { productId, quantity, type } = stockUpdate;

    // Validate quantity
    if (type === 'remove' && quantity > products.find(product => product.id === productId).quantity) {
      alert("Cannot remove more stock than available.");
      return;
    }

    // Add transaction and update stock
    addStockTransaction({
      productId,
      quantity: parseInt(quantity, 10),
      type,
      date: new Date().toLocaleString(),
    });
    setStockUpdate({ productId: '', quantity: 0, type: 'add' });
  };

  return (
    <div>
      <h1>Stock Management</h1>

      {/* Stock Update Form */}
      <form onSubmit={handleUpdateStock}>
        <select name="productId" value={stockUpdate.productId} onChange={handleChange} required>
          <option value="">Select Product</option>
          {products.map(product => (
            <option key={product.id} value={product.id}>{product.name}</option>
          ))}
        </select>
        <input
          type="number"
          name="quantity"
          value={stockUpdate.quantity}
          onChange={handleChange}
          required
          min="1"
        />
        <select name="type" value={stockUpdate.type} onChange={handleChange}>
          <option value="add">Add Stock</option>
          <option value="remove">Remove Stock</option>
        </select>
        <button type="submit">Update Stock</button>
      </form>

      {/* Current Stock Table */}
      <h2>Current Stock</h2>
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Stock Transactions Log */}
      <h2>Stock Transactions</h2>
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Type</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {/* Map transactions to display */}
        </tbody>
      </table>
    </div>
  );
};

export default StockManagement;
