import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useInventory } from '../context/InventoryContext';

const ProductManagement = () => {
  const { products, addProduct, updateProduct, deleteProduct, addStockTransaction } = useInventory();
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    quantity: '',
    category: '',
  });
  const [editingProduct, setEditingProduct] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      addProduct(response.data); // Update context with fetched products
      setError(''); // Clear any previous errors
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to fetch products. Please check the server.');
    }
  };

  const handleChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      if (editingProduct) {
        // Update existing product
        await axios.put(`http://localhost:5000/api/products/${editingProduct.id}`, newProduct);
        updateProduct({ ...editingProduct, ...newProduct }); // Update in context
        setEditingProduct(null); // Reset editing mode
      } else {
        // Add new product
        const response = await axios.post('http://localhost:5000/api/products', newProduct);
        addProduct(response.data); // Add new product to context
      }
      setNewProduct({ name: '', description: '', price: '', quantity: '', category: '' });
      setError('');
    } catch (err) {
      console.error('Error adding/updating product:', err);
      setError('Error adding/updating product. Please try again.');
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  const handleSellProduct = async (id) => {
    const product = products.find((p) => p.id === id);
    if (product && product.quantity > 0) {
      const updatedQuantity = product.quantity - 1;
      try {
        await axios.put(`http://localhost:5000/api/products/${id}`, {
          ...product,
          quantity: updatedQuantity,
        });
        addStockTransaction({ productId: id, quantity: 1, type: 'subtract' });
        setError('');
      } catch (err) {
        console.error('Error selling product:', err);
        setError('Failed to sell product. Please try again.');
      }
    } else {
      setError('Product is out of stock');
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`);
      deleteProduct(id); // Remove product from context
      setError('');
    } catch (err) {
      console.error('Error deleting product:', err.response || err.message);
      setError('Failed to delete product. Please try again.');
    }
  };

  const handleLogout = () => {
    navigate('/login');
  };

  const formatPrice = (price) => {
    const numericPrice = parseFloat(price);
    if (isNaN(numericPrice)) return '0';
    const priceInMillions = numericPrice / 1_000_000;
    return priceInMillions >= 1 ? `${priceInMillions.toFixed(1)}M` : `${numericPrice.toFixed(2)}`;
  };

  return (
    <div className="product-management">
      <header className="header">
        <h2>Product Management</h2>
      </header>

      {error && <p className="error">{error}</p>}

      <form onSubmit={handleAddProduct} className="product-form">
        <input type="text" name="name" value={newProduct.name} onChange={handleChange} placeholder="Product Name" required />
        <input type="text" name="description" value={newProduct.description} onChange={handleChange} placeholder="Description" required />
        <input type="text" name="category" value={newProduct.category} onChange={handleChange} placeholder="Category" required />
        <input type="number" name="price" value={newProduct.price} onChange={handleChange} placeholder="Price" step="0.01" required />
        <input type="number" name="quantity" value={newProduct.quantity} onChange={handleChange} placeholder="Quantity" required />
        <button type="submit">{editingProduct ? 'Update Product' : 'Add Product'}</button>
      </form>

      <h2>List of Products</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Description</th>
            <th>Category</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.description}</td>
              <td>{product.category}</td>
              <td>M {formatPrice(product.price)}</td>
              <td>{product.quantity}</td>
              <td>
                <button onClick={() => handleEditProduct(product)}>Edit</button>
                <button onClick={() => handleSellProduct(product.id)}>Sell</button>
                <button onClick={() => handleDeleteProduct(product.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductManagement;
