// src/components/ProductManagement.js
import React, { useState, useContext } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ProductManagement = () => {
  const { products, setProducts } = useContext(InventoryContext);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', price: '', quantity: '', category: '' });
  const [editingProduct, setEditingProduct] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      if (editingProduct) {
        await axios.put(`http://localhost:5000/api/products/${editingProduct.id}`, newProduct);
      } else {
        await axios.post('http://localhost:5000/api/products', newProduct);
      }
      fetchProducts(); // Fetch updated list of products
      setNewProduct({ name: '', description: '', price: '', quantity: '', category: '' });
      setEditingProduct(null);
      setError('');
    } catch (err) {
      console.error('Error adding/updating product:', err);
      setError('Error adding/updating product');
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`);
      fetchProducts(); // Fetch updated list of products
    } catch (err) {
      console.error('Error deleting product:', err);
      setError('Error deleting product');
    }
  };

  return (
    <div className="product-management">
      {/* Product form and product list (same as in your original code) */}
    </div>
  );
};

export default ProductManagement;
