import React, { useContext, useState } from 'react'; // Import React and necessary hooks
import { InventoryContext } from '../context/InventoryContext'; // Import InventoryContext
import '../styles.css'; // Import CSS for styling

const Login = () => {
  const { loginUser } = useContext(InventoryContext); // Access the loginUser function from context
  const [username, setUsername] = useState(''); // State for username
  const [password, setPassword] = useState(''); // State for password

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if username and password are provided
    if (username && password) {
      // Call the login function from context
      if (loginUser) {
        loginUser(); // Call the login function from context
      } else {
        console.error("loginUser is not defined"); // Log error if undefined
      }
    } else {
      alert("Please enter both username and password."); // Alert if fields are empty
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          required 
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
