import React, { createContext, useContext, useState } from 'react';

// Create InventoryContext
export const InventoryContext = createContext();

// InventoryProvider to wrap the app and manage products and transactions
export const InventoryProvider = ({ children }) => {
  const [products, setProducts] = useState([
    { id: '1', name: 'Product 1', quantity: 10 },
    { id: '2', name: 'Product 2', quantity: 20 },
  ]);

  const [transactions, setTransactions] = useState([]);

  // Function to add a stock transaction and update product quantity
  const addStockTransaction = ({ productId, quantity, type, date }) => {
    setTransactions((prevTransactions) => [
      ...prevTransactions,
      { productId, quantity, type, date },
    ]);

    // Update product quantity based on transaction type
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === productId
          ? {
              ...product,
              quantity:
                type === 'add'
                  ? product.quantity + quantity
                  : product.quantity - quantity,
            }
          : product
      )
    );
  };

  return (
    <InventoryContext.Provider value={{ products, transactions, addStockTransaction }}>
      {children}
    </InventoryContext.Provider>
  );
};
