// Dashboard.js

import React, { useContext } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import { Line } from 'react-chartjs-2';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Dashboard.css';

// Importing local images for carousel
import Product1 from '../assets/images/Product1.jpg';
import Product2 from '../assets/images/Product2.jpg';
import Product3 from '../assets/images/Product3.jpg';

const Dashboard = () => {
  const { products } = useContext(InventoryContext);
  const lowStockThreshold = 10;

  // Calculate low stock products
  const lowStockProducts = products.filter(product => product.quantity < lowStockThreshold);

  // Prepare data for stock overview chart
  const stockData = {
    labels: products.map((product) => product.name),
    datasets: [
      {
        label: 'Stock Level',
        data: products.map((product) => product.quantity),
        backgroundColor: 'rgba(246, 133, 162, 0.2)',
        borderColor: '#f85d85', // More visible line color
        borderWidth: 3,
        pointBackgroundColor: '#f85d85', // Point color on line
        pointRadius: 5, // Larger points for visibility
      },
    ],
  };

  const stockOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Stock Quantity',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Products',
        },
      },
    },
  };

  // Array of local images for carousel
  const imageUrls = [Product1, Product2, Product3];

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      {/* Stock Overview Section */}
      <div className="stock-overview">
        <h2>Current Stock Levels</h2>
        <div className="chart-container">
          <Line data={stockData} options={stockOptions} />
        </div>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              <strong>{product.name}</strong>: {product.quantity} in stock
            </li>
          ))}
        </ul>
        <p>Total Products: {products.length}</p>
      </div>

      {/* Low Stock Alerts */}
      {lowStockProducts.length > 0 && (
        <div className="low-stock-alert">
          <h2>Low Stock Alerts</h2>
          <ul>
            {lowStockProducts.map((product) => (
              <li key={product.id}>
                <strong>{product.name}</strong>: Only {product.quantity} left in stock!
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Rotating Images Section */}
      <div className="stock-carousel">
        <h2>Featured Products</h2>
        <Carousel
          autoPlay
          infiniteLoop
          interval={3000}
          showThumbs={false}
          showStatus={false}
          dynamicHeight={true}
        >
          {imageUrls.map((url, index) => (
            <div key={index}>
              <img src={url} alt={`Product ${index + 1}`} />
            </div>
          ))}
        </Carousel>
      </div>
    </div>
  );
};

export default Dashboard;
