import React, { createContext, useState, useEffect } from 'react';

export const InventoryContext = createContext();

export const InventoryProvider = ({ children }) => {
  const [products, setProducts] = useState(() => {
    const storedProducts = localStorage.getItem('products');
    return storedProducts ? JSON.parse(storedProducts) : [];
  });
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    localStorage.setItem('products', JSON.stringify(products));
  }, [products]);

  const addProduct = (product) => {
    setProducts([...products, product]);
  };

  const updateProductQuantity = (productId, quantityChange) => {
    setProducts(products.map(product => {
      if (product.id === productId) {
        const updatedQuantity = product.quantity + quantityChange;
        console.log(`Updated ${product.name}: ${product.quantity} -> ${updatedQuantity}`); // Log change
        return { ...product, quantity: updatedQuantity };
      }
      return product;
    }));
  };

  const addStockTransaction = (transaction) => {
    const quantityChange = transaction.type === 'add' ? 
      parseInt(transaction.quantity) : 
      -parseInt(transaction.quantity);

    console.log("Before Update: ", products);  // Log current products before update
    updateProductQuantity(transaction.productId, quantityChange);
    console.log("After Update: ", products);   // Log current products after update

    setTransactions([...transactions, transaction]); // Log the transaction
  };

  return (
    <InventoryContext.Provider value={{
      products, addProduct,
      transactions, addStockTransaction
    }}>
      {children}
    </InventoryContext.Provider>
  );
};
