import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const ProductManagement = () => {
  const { products, addProduct } = useContext(InventoryContext);
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    category: '',
    price: 0,
    quantity: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewProduct(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const productId = new Date().getTime().toString(); // Unique ID based on timestamp
    addProduct({ id: productId, ...newProduct });
    setNewProduct({ name: '', description: '', category: '', price: 0, quantity: 0 });
  };

  return (
    <div>
      <h1>Product Management</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" value={newProduct.name} onChange={handleChange} placeholder="Product Name" required />
        <input type="text" name="description" value={newProduct.description} onChange={handleChange} placeholder="Description" required />
        <input type="text" name="category" value={newProduct.category} onChange={handleChange} placeholder="Category" required />
        <input type="number" name="price" value={newProduct.price} onChange={handleChange} placeholder="Price" required />
        <input type="number" name="quantity" value={newProduct.quantity} onChange={handleChange} placeholder="Initial Quantity" required />
        <button type="submit">Add Product</button>
      </form>

      {/* Product Table */}
      <h2>Products</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Category</th>
            <th>Price</th>
            <th>Initial Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.description}</td>
              <td>{product.category}</td>
              <td>{product.price}</td>
              <td>{product.initialquantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductManagement;
