import React, { useState, useContext, useEffect } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const ProductManagement = () => {
  const { products, addProduct } = useContext(InventoryContext);
  const [newProduct, setNewProduct] = useState({ name: '', quantity: 0 });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleAddProduct = () => {
    addProduct(newProduct);
    setNewProduct({ name: '', quantity: 0 }); // Reset fields after adding
  };

  return (
    <div>
      <h2>Product Management</h2>
      <div>
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={handleChange}
          style={{ width: '200px', height: '30px' }}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={newProduct.quantity}
          onChange={handleChange}
          style={{ width: '100px', height: '30px' }}
        />
        <button onClick={handleAddProduct} style={{ height: '35px' }}>Add Product</button>
      </div>

      <table border="1" style={{ width: '100%', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={index}>
              <td>{product.name}</td>
              <td>{product.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductManagement;
