import React, { useContext } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import { Line } from 'react-chartjs-2';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import './Dashboard.css';

// Import the necessary Chart.js components and register them
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// Register the components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const { products } = useContext(InventoryContext);
  const lowStockThreshold = 10;

  // Calculate low stock products
  const lowStockProducts = products.filter(product => product.quantity < lowStockThreshold);

  // Prepare data for stock overview chart
  const stockData = {
    labels: products.map((product) => product.name),
    datasets: [
      {
        label: 'Stock Level',
        data: products.map((product) => product.quantity),
        backgroundColor: 'rgba(246, 133, 162, 0.2)',
        borderColor: '#f685a2',
        borderWidth: 2,
        pointBackgroundColor: '#e67299',
      },
    ],
  };

  const stockOptions = {
    responsive: true,
    maintainAspectRatio: true, // Ensure aspect ratio is maintained
    aspectRatio: 2, // Adjust aspect ratio (2 means width will be 2x height)
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Stock Quantity',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Products',
        },
      },
    },
    layout: {
      padding: {
        left: 20,
        right: 20,
        top: 20,
        bottom: 20,
      },
    },
    elements: {
      line: {
        tension: 0.4, // Smooth the line
      },
    },
    animation: {
      duration: 500,
    },
  };

  // Array of image URLs for the carousel (use actual product images if available)
  const imageUrls = [
    'https://via.placeholder.com/600x400?text=Product+1',
    'https://via.placeholder.com/600x400?text=Product+2',
    'https://via.placeholder.com/600x400?text=Product+3',
  ];

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      {/* Stock Overview Section */}
      <div className="stock-overview">
        <h2>Current Stock Levels</h2>
        <Line data={stockData} options={stockOptions} />
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              <strong>{product.name}</strong>: {product.quantity} in stock
            </li>
          ))}
        </ul>
        <p>Total Products: {products.length}</p>
      </div>

      {/* Low Stock Alerts */}
      {lowStockProducts.length > 0 && (
        <div className="low-stock-alert">
          <h2>Low Stock Alerts</h2>
          <ul>
            {lowStockProducts.map((product) => (
              <li key={product.id}>
                <strong>{product.name}</strong>: Only {product.quantity} left in stock!
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Rotating Images Section */}
      <div className="stock-carousel">
        <h2>Featured Products</h2>
        <Carousel
          autoPlay
          infiniteLoop
          interval={3000}
          showThumbs={false}
          showStatus={false}
        >
          {imageUrls.map((url, index) => (
            <div key={index}>
              <img src={url} alt={`Product ${index + 1}`} />
            </div>
          ))}
        </Carousel>
      </div>
    </div>
  );
};

export default Dashboard;
