import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const UserManagement = () => {
  const { users, addUser, updateUser, deleteUser } = useContext(InventoryContext);
  const [userForm, setUserForm] = useState({ name: '', email: '' });
  const [isEditing, setIsEditing] = useState(false);
  const [editingUserId, setEditingUserId] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      updateUser({ id: editingUserId, ...userForm });
      setIsEditing(false);
      setEditingUserId(null);
    } else {
      const userId = new Date().getTime().toString();
      addUser({ id: userId, ...userForm });
    }
    setUserForm({ name: '', email: '' });
  };

  const handleEdit = (user) => {
    setUserForm(user);
    setIsEditing(true);
    setEditingUserId(user.id);
  };

  const handleDelete = (id) => {
    deleteUser(id);
  };

  return (
    <div>
      <h1>User Management</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" value={userForm.name} onChange={handleChange} placeholder="Name" required />
        <input type="email" name="email" value={userForm.email} onChange={handleChange} placeholder="Email" required />
        <button type="submit">{isEditing ? 'Update User' : 'Add User'}</button>
      </form>

      <h2>Users</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => handleEdit(user)}>Edit</button>
                <button onClick={() => handleDelete(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserManagement;
