// src/components/Dashboard.js

import React, { useContext } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import StockChart from './StockChart';  // Import the StockChart component

const Dashboard = () => {
  const { products } = useContext(InventoryContext);
  const lowStockThreshold = 10;

  // Prepare data for the StockChart component
  const chartData = {
    labels: products.map(product => product.name),  // Product names as labels
    values: products.map(product => product.quantity),  // Stock quantities as values
  };

  // Filter out low-stock products based on the lowStockThreshold
  const lowStockProducts = products.filter(product => product.quantity < lowStockThreshold);

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      {/* Stock Overview Chart */}
      <div className="stock-overview">
        <h2>Current Stock Levels</h2>
        <StockChart data={chartData} />  {/* Add the StockChart here */}
      </div>

      {/* Low Stock Alerts */}
      {lowStockProducts.length > 0 && (
        <div className="low-stock-alert">
          <h2>Low Stock Alerts</h2>
          <ul>
            {lowStockProducts.map((product) => (
              <li key={product.id}>
                <strong>{product.name}</strong>: Only {product.quantity} left in stock!
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* List of all products */}
      <div className="stock-overview">
        <h2>All Products</h2>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              <strong>{product.name}</strong>: {product.quantity} in stock
            </li>
          ))}
        </ul>
        <p>Total Products: {products.length}</p>
      </div>
    </div>
  );
};

export default Dashboard;
