import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const ProductManagement = () => {
  const { addProduct } = useContext(InventoryContext);
  const [productForm, setProductForm] = useState({
    name: '',
    quantity: 0,
  });

  const handleChange = (e) => {
    setProductForm({ ...productForm, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const productId = Date.now().toString(); // Generate a unique ID for the product
    addProduct({ id: productId, ...productForm }); // Call addProduct
    setProductForm({ name: '', quantity: 0 }); // Reset form
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={productForm.name}
        onChange={handleChange}
        placeholder="Product Name"
        required
      />
      <input
        type="number"
        name="quantity"
        value={productForm.quantity}
        onChange={handleChange}
        placeholder="Quantity"
        required
      />
      <button type="submit">Add Product</button>
    </form>
  );
};

export default ProductManagement;
