import React, { useState } from 'react';

const StockManagement = () => {
    // State to hold transactions
    const [transactions, setTransactions] = useState([]);
    const [itemName, setItemName] = useState('');
    const [itemQuantity, setItemQuantity] = useState(0);
    const [transactionType, setTransactionType] = useState('add'); // 'add' or 'deduct'

    // Function to handle adding or deducting a transaction
    const recordTransaction = (e) => {
        e.preventDefault();
        if (itemName && itemQuantity > 0) {
            const newTransaction = {
                name: itemName,
                quantity: transactionType === 'add' ? itemQuantity : -itemQuantity,
            };
            setTransactions((prevTransactions) => [...prevTransactions, newTransaction]);
            // Clear input fields
            setItemName('');
            setItemQuantity(0);
        } else {
            alert("Please enter a valid item name and quantity.");
        }
    };

    // Function to handle item name change
    const handleNameChange = (e) => {
        setItemName(e.target.value);
    };

    // Function to handle item quantity change
    const handleQuantityChange = (e) => {
        setItemQuantity(Number(e.target.value));
    };

    // Function to handle transaction type change
    const handleTransactionTypeChange = (e) => {
        setTransactionType(e.target.value);
    };

    return (
        <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px', width: '400px' }}>
            <h1>Stock Management</h1>
            <form onSubmit={recordTransaction}>
                <div>
                    <label htmlFor="itemName">Item Name:</label>
                    <input
                        type="text"
                        id="itemName"
                        value={itemName}
                        onChange={handleNameChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="itemQuantity">Quantity:</label>
                    <input
                        type="number"
                        id="itemQuantity"
                        value={itemQuantity}
                        onChange={handleQuantityChange}
                        min="1"
                        required
                    />
                </div>
                <div>
                    <label>Transaction Type:</label>
                    <select value={transactionType} onChange={handleTransactionTypeChange}>
                        <option value="add">Add</option>
                        <option value="deduct">Deduct</option>
                    </select>
                </div>
                <button type="submit">Record Transaction</button>
            </form>

            <h2>Transactions:</h2>
            {transactions.length > 0 ? (
                <ul>
                    {transactions.map((transaction, index) => (
                        <li key={index}>
                            {transaction.name} - Quantity: {transaction.quantity > 0 ? `+${transaction.quantity}` : transaction.quantity}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No transactions available.</p>
            )}
        </div>
    );
};

export default StockManagement;
