import React, { useContext, useState } from 'react';
import axios from 'axios';
import { InventoryContext } from '../context/InventoryContext';
import { useNavigate, Link } from 'react-router-dom';
import '../styles.css';

const Login = () => {
  const { loginUser } = useContext(InventoryContext);
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { username, password } = credentials;

    try {
      // First, try to register the user by calling the signup API
      const signupResponse = await axios.post('http://localhost:5000/api/signup', { username, password });

      // If registration is successful, proceed to login
      if (signupResponse.status === 200) {
        const loginResponse = await axios.post('http://localhost:5000/api/login', { username, password });

        // If login is successful, call loginUser and navigate to the dashboard
        if (loginResponse.status === 200) {
          const success = loginUser(credentials);

          if (success) {
            navigate('/'); // Redirect to the dashboard on successful login
          } else {
            setError('Login failed after registration');
          }
        } else {
          setError('Login failed');
        }
      }
    } catch (err) {
      if (err.response && err.response.status === 400) {
        // Handle errors during signup or login (e.g., user already exists)
        setError('User registration failed or login error: ' + (err.response.data || 'Unknown error'));
      } else {
        setError('Error: ' + (err.response ? err.response.data : 'Unknown error'));
      }
    }
  };

  return (
    <div className="container">
      <div className="loginBox">
        <div className="loginHead">
          <h1>WINGS CAFE</h1>
          <p>Stock Inventory System</p>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="loginInputContainer">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={credentials.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="loginInputContainer">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={credentials.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="LoginButtonContainer">
            <button type="submit">Login</button>
          </div>
          <p>
            Already have an account? <Link to="/login">Login here</Link>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
