import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';
import '../styles.css';

const StockManagement = () => {
  const { products, addStockTransaction } = useContext(InventoryContext);
  const [transaction, setTransaction] = useState({ productId: '', quantity: '', type: '' });

  const handleChange = (e) => {
    setTransaction({ ...transaction, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addStockTransaction(transaction);
    // Logic to update stock based on transaction type can be added here
    setTransaction({ productId: '', quantity: '', type: '' });
  };

  return (
    <div className="stock-management">
      <h2>Stock Management</h2>
      <form>
        {/* Stock input fields */}
        <label>Product:</label>
        <select>
          <option>Product 1</option>
          <option>Product 2</option>
        </select>

        <label>Quantity to Add:</label>
        <input type="number" placeholder="Enter quantity to add" />

        <button type="submit">Update Stock</button>
      </form>
    </div>
  );
};

export default StockManagement;
