import React, { useContext, useState } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const StockManagement = () => {
  const { products, addStockTransaction } = useContext(InventoryContext);
  const [stockUpdate, setStockUpdate] = useState({
    productId: '',
    quantity: 0,
    type: 'add', // or 'subtract'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStockUpdate(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addStockTransaction({
      productId: stockUpdate.productId,
      quantity: parseInt(stockUpdate.quantity, 10),
      type: stockUpdate.type,
    });
    setStockUpdate({ productId: '', quantity: 0, type: 'add' }); // Reset form
  };

  return (
    <div>
      <h1>Stock Management</h1>
      <form onSubmit={handleSubmit}>
        <select name="productId" value={stockUpdate.productId} onChange={handleChange} required>
          <option value="">Select Product</option>
          {products.map(product => (
            <option key={product.id} value={product.id}>
              {product.name}
            </option>
          ))}
        </select>
        <input 
          type="number" 
          name="quantity" 
          value={stockUpdate.quantity} 
          onChange={handleChange} 
          placeholder="Quantity" 
          required 
        />
        <select name="type" value={stockUpdate.type} onChange={handleChange}>
          <option value="add">Add Stock</option>
          <option value="subtract">Subtract Stock</option>
        </select>
        <button type="submit">Update Stock</button>
      </form>

      {/* Transactions Table */}
      <h2>Stock Transactions</h2>
      <table>
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Quantity</th>
            <th>Type</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.quantity}</td>
              <td>{product.type}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockManagement;
