import React, { useState, useContext } from 'react';
import { InventoryContext } from '../context/InventoryContext';

const StockManagement = () => {
  const { products, updateStock } = useContext(InventoryContext);
  const [stockUpdate, setStockUpdate] = useState({ id: null, quantity: 0 });

  const handleUpdateChange = (e) => {
    const { name, value } = e.target;
    setStockUpdate({ ...stockUpdate, [name]: value });
  };

  const handleUpdateStock = (id) => {
    updateStock(id, parseInt(stockUpdate.quantity, 10));
    setStockUpdate({ id: null, quantity: 0 }); // Reset after update
  };

  return (
    <div>
      <h2>Stock Management</h2>
      <table border="1" style={{ width: '100%', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Current Quantity</th>
            <th>Update Stock</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={index}>
              <td>{product.name}</td>
              <td>{product.quantity}</td>
              <td>
                <input
                  type="number"
                  name="quantity"
                  placeholder="New Quantity"
                  value={stockUpdate.id === index ? stockUpdate.quantity : ''}
                  onChange={handleUpdateChange}
                  style={{ width: '100px', height: '30px' }}
                />
                <button
                  onClick={() => handleUpdateStock(index)}
                  style={{ height: '35px', marginLeft: '5px' }}
                >
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockManagement;
