// Login.js
import React, { useContext, useState } from 'react';
import axios from 'axios';
import { InventoryContext } from '../context/InventoryContext';
import { useNavigate, Link } from 'react-router-dom';
import '../styles.css';

const Login = () => {
  const { loginUser, registerUser } = useContext(InventoryContext);
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [isLogin, setIsLogin] = useState(true); // Track whether it's login or register mode
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { username, password } = credentials;

    if (!isLogin && password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      if (isLogin) {
        // Handle login request
        const response = await axios.post('http://localhost:5000/api/login', { username, password });
        if (response.status === 200 && loginUser(credentials)) {
          navigate('/'); // Redirect to the dashboard
        } else {
          setError('Invalid credentials');
        }
      } else {
        // Handle registration request
        await registerUser(credentials);
        navigate('/login'); // Redirect to login after registration
      }
    } catch (err) {
      setError('Error: ' + (err.response ? err.response.data : 'Unknown error'));
    }
  };

  return (
    <div className="container">
      <div className="loginBox">
        <div className="loginHead">
          <h1>{isLogin ? 'WINGS CAFE' : 'Register'}</h1>
          <p>{isLogin ? 'Stock Inventory System' : 'Create a new account'}</p>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="loginInputContainer">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={credentials.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="loginInputContainer">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={credentials.password}
              onChange={handleChange}
              required
            />
          </div>
          {!isLogin && (
            <div className="loginInputContainer">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>
          )}
          <div className="LoginButtonContainer">
            <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
          </div>
          <p>
            {isLogin ? (
              <>
                Don't have an account?{' '}
                <span onClick={() => setIsLogin(false)} style={{ cursor: 'pointer', color: 'blue' }}>
                  Register here
                </span>
              </>
            ) : (
              <>
                Already have an account?{' '}
                <span onClick={() => setIsLogin(true)} style={{ cursor: 'pointer', color: 'blue' }}>
                  Login here
                </span>
              </>
            )}
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
